import { Component } from '@angular/core';
import { AuthService } from './services/auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'reddit';
  isAuth: boolean;
  constructor(private authService: AuthService) { }

  ngOnInit() {
    this.authService.isAuthenticated$.subscribe(isAuth => {
      this.isAuth = isAuth;
    });
  }
}
<div class="header">
  <a href="/"><img src="../assets/Reddit-Logo.png" alt="Reddit Logo" class="logo"></a>
  <div class="search-bar">
    <input type="text" placeholder="Search...">
    <button>Search</button>
  </div>
  <div class="actions">
    <app-login *ngIf="!isAuth" routerLink="/login"></app-login>
    <button class="login" *ngIf="!isAuth" routerLink="/login">Login</button>
    <button class="register" *ngIf="!isAuth" routerLink="/register">Register</button>
    <button class="logout" *ngIf="isAuth" (click)="authService.logout()" routerLink="/logout" >Logout</button>
  </div>
</div>
  
<div class="content">
  <app-post-list></app-post-list>
</div>
